import React, { createContext, useEffect, Provider } from "react";

import { useColorMode } from "@chakra-ui/core";

export const DarkModeContext = createContext<Array<any> | undefined>(undefined);

export const DarkModeProvider = ({ children }) => {
    const { colorMode, toggleColorMode } = useColorMode();

    const providerValue: Array<("light" | "dark") | (() => void)> = [
        colorMode,
        toggleColorMode,
    ];

    return (
        <DarkModeContext.Provider value={providerValue}>
            {children}
        </DarkModeContext.Provider>
    );
};
