import { AppProps } from "next/app";
import cookies from "next-cookies";

import { ThemeProvider, CSSReset, ColorModeProvider } from "@chakra-ui/core";
import { ColorModeProvider as MyColorModeProvider } from "../Handlers/ColorModeProvider";
import Layout from "../Components/Layout";

interface InitialAppProps extends AppProps {
    initialColorMode: string;
}

const App = ({ Component, pageProps, initialColorMode }: InitialAppProps) => {
    return (
        <ThemeProvider>
            <ColorModeProvider>
                {/* 
                // @ts-ignore */}
                <MyColorModeProvider value={initialColorMode}>
                    <CSSReset />
                    <Layout>
                        <Component {...pageProps} />
                    </Layout>
                </MyColorModeProvider>
            </ColorModeProvider>
        </ThemeProvider>
    );
};

App.getInitialProps = async ({ Component, ctx }) => {
    let pageProps = {};
    if (Component.getInitialProps) {
        pageProps = await Component.getInitialProps(ctx);
    }
    // const { isDarkMode = "false" } = cookies(ctx);
    const isDarkMode = "true";
    return {
        pageProps,
        initialColorMode: isDarkMode === "true" ? "dark" : "light",
    };
};

export default App;
