import React, { createContext, useEffect, Provider } from "react";

import { useColorMode } from "@chakra-ui/core";

type callback = () => void;
type ProviderProps = Array<("light" | "dark") | callback>;

export const DarkModeContext = createContext<ProviderProps | undefined>(
    undefined
);

export const DarkModeProvider = ({ children }) => {
    const { colorMode, toggleColorMode } = useColorMode();

    const providerValue: ProviderProps = [colorMode, toggleColorMode];

    return (
        <DarkModeContext.Provider value={providerValue}>
            {children}
        </DarkModeContext.Provider>
    );
};
