import { AppProps } from "next/app";
import cookies from "next-cookies";

import {
    ThemeProvider,
    CSSReset,
    ColorModeProvider,
    useColorMode,
} from "@chakra-ui/core";
import Layout from "../Components/Layout";

interface InitialAppProps extends AppProps {
    initialColorMode: string;
}

const App = ({ Component, pageProps, initialColorMode }) => {
    return (
        <ThemeProvider>
            <ColorModeProvider value={initialColorMode}>
                <CSSReset />
                <Layout>
                    <Component {...pageProps} />
                </Layout>
            </ColorModeProvider>
        </ThemeProvider>
    );
};

App.getInitialProps = async ({ Component, ctx }) => {
    let pageProps = {};
    if (Component.getInitialProps) {
        pageProps = await Component.getInitialProps(ctx);
    }
    const { isDarkMode = "true" } = cookies(ctx);
    return {
        pageProps,
        initialColorMode: isDarkMode === "true" ? "dark" : "light",
    };
};

export default App;
