import { useColorMode } from "@chakra-ui/core";

export const toggleDarkMode = () => {
    const { colorMode, toggleColorMode } = useColorMode();

    const handleToggleDarkMode = () => {
        toggleColorMode();
        document.cookie = `isDarkMode=${colorMode === "light"}`;
    };
};
