import { useColorMode } from "@chakra-ui/core";

import React, { createContext, useContext } from "react";

import { useCookies } from "react-cookie";

const ColorModeContext = createContext<"light" | "dark">("light");

interface Props {
    children: React.ReactNode;
}

const ColorModeProvider = (props: Props) => {
    const { children } = props;

    const { colorMode, toggleColorMode } = useColorMode();

    return (
        <ColorModeContext.Provider value="light">
            {children}
        </ColorModeContext.Provider>
    );
};

const useDarkMode = () => useContext(ColorModeContext);

export { ColorModeProvider, useDarkMode };
