import { AppProps } from "next/app";

import { ThemeProvider, CSSReset, ColorModeProvider } from "@chakra-ui/core";
import Layout from "../Components/Layout";

const App = ({ Component, pageProps }: AppProps) => {
    return (
        <ThemeProvider>
            <ColorModeProvider>
                <CSSReset />
                <Layout>
                    <Component {...pageProps} />
                </Layout>
            </ColorModeProvider>
        </ThemeProvider>
    );
};

export default App;
